using BinaryProvider # requires BinaryProvider 0.3.0 or later

# Parse some basic command-line arguments
const verbose = "--verbose" in ARGS
const prefix = Prefix(get([a for a in ARGS if a != "--verbose"], 1, joinpath(@__DIR__, "usr")))
products = [
    LibraryProduct(prefix, ["libfreetype"], :libfreetype),
]

# Download binaries from hosted location
bin_prefix = "https://github.com/JuliaGraphics/FreeTypeBuilder/releases/download/v2.9.1-4"

# Listing of files generated by BinaryBuilder:
download_info = Dict(
    Linux(:aarch64, libc=:glibc) => ("$bin_prefix/FreeType2.v2.10.0.aarch64-linux-gnu.tar.gz", "f254c5b54fce98c77630a6eeaed17453490e3c3b7a0ae356066315ad0fd53985"),
    Linux(:aarch64, libc=:musl) => ("$bin_prefix/FreeType2.v2.10.0.aarch64-linux-musl.tar.gz", "fdbf88b5c7a31ebdab7b234f6a7fedfaa830ef0371a226a0d2f4a3b3d260238a"),
    Linux(:armv7l, libc=:glibc, call_abi=:eabihf) => ("$bin_prefix/FreeType2.v2.10.0.arm-linux-gnueabihf.tar.gz", "e5c9a74e49b4206b7c5a54237c1a12d3dbf62122509b7bb0ccb91813cfd6a2fc"),
    Linux(:armv7l, libc=:musl, call_abi=:eabihf) => ("$bin_prefix/FreeType2.v2.10.0.arm-linux-musleabihf.tar.gz", "ad4278693844e1e0e8d7451e19f93ee253559fb6162477defd9e1ca71248f225"),
    Linux(:i686, libc=:glibc) => ("$bin_prefix/FreeType2.v2.10.0.i686-linux-gnu.tar.gz", "9c38374d185d6c44233514af7aa53ddbbd2772afd3377f82c9774886919ceaa9"),
    Linux(:i686, libc=:musl) => ("$bin_prefix/FreeType2.v2.10.0.i686-linux-musl.tar.gz", "4b519ce9d4d8cf50a8db64e8d20a56617fe5f360ce2379e235fd550b7bbe3122"),
    Windows(:i686) => ("$bin_prefix/FreeType2.v2.10.0.i686-w64-mingw32.tar.gz", "d8e3581cd14d63cfc7ef8f9b6101d7fad3557fd3e94afb2b27f1f810b728c36c"),
    Linux(:powerpc64le, libc=:glibc) => ("$bin_prefix/FreeType2.v2.10.0.powerpc64le-linux-gnu.tar.gz", "e8f0c8cbb849f8c9ca0b68d4883ae7f4ab079f8e70ee8d0203033b2c7586c0d7"),
    MacOS(:x86_64) => ("$bin_prefix/FreeType2.v2.10.0.x86_64-apple-darwin14.tar.gz", "11546abe4189f586f96a3aa272d9ee3ae85b2c0110e9a149242806f6f9c39231"),
    Linux(:x86_64, libc=:glibc) => ("$bin_prefix/FreeType2.v2.10.0.x86_64-linux-gnu.tar.gz", "e772cf85766f20002e17c99a0ebc045dcdda5f65aa390acd8f4b3792f2f7432f"),
    Linux(:x86_64, libc=:musl) => ("$bin_prefix/FreeType2.v2.10.0.x86_64-linux-musl.tar.gz", "d90c08f1f1d3819fa7389a1834a0cba8b5daa117eee2587dcc3cda162ab898c6"),
    FreeBSD(:x86_64) => ("$bin_prefix/FreeType2.v2.10.0.x86_64-unknown-freebsd11.1.tar.gz", "37973d55c59549fb0c4623db4aa5c618b116937deba9e33a86cf1de379018b57"),
    Windows(:x86_64) => ("$bin_prefix/FreeType2.v2.10.0.x86_64-w64-mingw32.tar.gz", "a992aac921e612084608cd228eeadbda2444201bcdf35d999443f3776d498485"),
)

# Install unsatisfied or updated dependencies:
unsatisfied = any(!satisfied(p; verbose=verbose) for p in products)
dl_info = choose_download(download_info, platform_key_abi())
if dl_info === nothing && unsatisfied
    # If we don't have a compatible .tar.gz to download, complain.
    # Alternatively, you could attempt to install from a separate provider,
    # build from source or something even more ambitious here.
    error("Your platform (\"$(Sys.MACHINE)\", parsed as \"$(triplet(platform_key_abi()))\") is not supported by this package!")
end

# If we have a download, and we are unsatisfied (or the version we're
# trying to install is not itself installed) then load it up!
if unsatisfied || !isinstalled(dl_info...; prefix=prefix)
    # Download and install binaries
    install(dl_info...; prefix=prefix, force=true, verbose=verbose)
end

# Write out a deps.jl file that will contain mappings for our products
write_deps_file(joinpath(@__DIR__, "deps.jl"), products, verbose=verbose)
